import React, { useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const googleLogin = () => {
    console.log("Logging in with Google");
    window.location.href = 'http://localhost:8080/login/google';
  };

  const githubLogin = () => {
    console.log("Logging in with GitHub");
    window.location.href = 'http://localhost:8080/login/github';
  };

  const handleLocalLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8080/login/local', {
        email,
        password
      });
      navigate('/home');
      alert('Login successful!');
    } catch (error) {
      alert('Login failed!');
    }
  };

  return (
    <div className="auth-container">
      <h2>Login</h2>
      <form className="auth-form" onSubmit={handleLocalLogin}>
        <input 
          type="email" 
          value={email} 
          onChange={(e) => setEmail(e.target.value)} 
          required 
          placeholder='Enter Email ...' 
        />
        <input 
          type="password" 
          value={password} 
          onChange={(e) => setPassword(e.target.value)} 
          required  
          placeholder='Enter Password ...' 
        />
        <button type="submit">Login</button>
      </form>
      <div className="oauth-login">
        <p>Or login with:</p>
        <button onClick={googleLogin}>Continue with Google</button>
        <button onClick={githubLogin}>Continue with GitHub</button>
        <p>Donâ€™t have an account? <Link to="/register">Register here</Link></p>
      </div>
    </div>
  );
}

export default Login;
