import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const ViewResources = () => {
  const { id } = useParams();
  const [resource, setResource] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:5050/api/resources/${id}`)
      .then(res => setResource(res.data))
      .catch(err => console.error('Error fetching resource:', err));
  }, [id]);

  if (!resource) return <div>Loading...</div>;

  return (
    <div style={{ padding: '20px' }}>
      <h2>{resource.title} ({resource.type})</h2>
      <p><strong>Description:</strong> {resource.description}</p>
      <p><strong>Content:</strong> {resource.content}</p>
      <p><strong>Prerequisites:</strong> {resource.prerequisites}</p>
    </div>
  );
};

export default ViewResources;
