import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
const ViewResources = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  <button onClick={() => navigate(`/resources/update/${resource.resourceId}`)}>
  Edit
</button>
  const [resource, setResource] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:5050/api/resources/${id}`, { withCredentials: true })
      .then(res => {
        setResource(res.data);
        setLoading(false);
      })
      .catch(err => {
        setLoading(false);
        // If error is 401 or 403, redirect to login
        if (err.response && (err.response.status === 401 || err.response.status === 403)) {
          window.location.href = "http://localhost:5050/oauth2/authorization/google";
        } else {
          setError('Error fetching resource');
        }
      });
  }, [id]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;
  if (!resource) return <div>No resource found.</div>;

  return (
    <div style={{ padding: '20px' }}>
      <h2>{resource.title} ({resource.type})</h2>
      <p><strong>Description:</strong> {resource.description}</p>
      <p><strong>Content:</strong> {resource.content}</p>
      <p><strong>Prerequisites:</strong> {resource.prerequisites}</p>
    </div>
  );
  
};

export default ViewResources;
