import React, { useState } from 'react';
import axios from 'axios';

const CreateResource = () => {
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    content: '',
    prerequisites: '',
    type: 'Course'
  });

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5050/api/resources", formData);
      if (response.status === 200) {
        // Redirect to the resources page after successful creation
        window.location.href = 'http://localhost:3000/resources';
      }
    } catch (error) {
      console.error('Error creating resource:', error);
    }
  };
  

  return (
    <form onSubmit={handleSubmit}>
      <h2>Create New Resource</h2>
      <input name="title" placeholder="Title" value={formData.title} onChange={handleChange} required />
      <textarea name="description" placeholder="Description" value={formData.description} onChange={handleChange} />
      <textarea name="content" placeholder="Content" value={formData.content} onChange={handleChange} />
      <input name="prerequisites" placeholder="Prerequisites" value={formData.prerequisites} onChange={handleChange} />
      <select name="type" value={formData.type} onChange={handleChange}>
        <option value="Course">Course</option>
        <option value="Tutorial">Tutorial</option>
      </select>
      <button type="submit">Create</button>
    </form>
  );
};

export default CreateResource;
