import React, { useEffect, useState } from 'react';
import axios from 'axios';
import CreateResource from './CreateResource'; // Adjust the import path as needed

const ViewResources = () => {
  const [resources, setResources] = useState([]);

  useEffect(() => {
    fetchResources();
  }, []);

  const fetchResources = () => {
    axios.get('http://localhost:5050/api/resources')
      .then(res => setResources(res.data))
      .catch(err => console.error('Error fetching resources:', err));
  };

  // Add new resource to the top of the list
  const handleResourceCreated = (newResource) => {
    setResources(prev => [newResource, ...prev]);
  };

  return (
    <div style={{ padding: '20px' }}>
      <CreateResource onResourceCreated={handleResourceCreated} />

      <h2>All Courses & Tutorials</h2>
      <ul>
        {resources.map(resource => (
          <li key={resource.resourceId}>
            <strong>{resource.title}</strong> - {resource.type}
            <p>{resource.description}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ViewResources;
